mapper.select:
 SELECT  	ID
	FROM EMP
    WHERE EMP_NO= #{id}

mapper.insert:
INSERT INTO EMP
			(
			EMP_NO,
			ID,
			PW,
			NAME,
			TEL,
			ENTER_CD,
			AUTH_NO,
			DPT

			)
    VALUES  (
    		  SEQ_EMP.nextval 
    		,#{id}
    		,#{pw}
    		,#{name}
    		,#{tel}
    		,#{enterCd}
    		,#{authNo}
    		,#{dpt}
    		)
