mapper.select:
 SELECT  	ID
	FROM ENTER
    WHERE ID= #{id}

mapper.insert:
	INSERT INTO ENTER
				(
				ENTER_CD,
                ID,
                PW,
                BUSSNO,
                ENTER_NAME,
                CHG,
                MAIL,
                CEO,
                AUTH_NO

				)
	    VALUES  (
	    		  SEQ_ENTER.nextval 
	    		,#{id}
	    		,#{pw}
	    		,#{bussno}
	    		,#{enterName}
	    		,#{chg}
	    		,#{mail}
	    		,#{ceo}
	    		,#{authNo}
	    		)
