mapper.insert:
INSERT INTO FC_INS
			(
			INS_CD,
			INS_DT,
			INS_CAT,
			INS_CONT,
			CC,
			CHG,
			FC_CD

			)
    VALUES  (
    		  SEQ_FCINSP.nextval 
    		,#{insDt}
    		,#{insCat}
    		,#{insCont}
    		,#{cc}
    		,#{chg}
    		,#{fcCd}
    		)

mapper.update:
UPDATE FC_INS
		SET
				INS_CONT =#{insCont} ,
				CC = #{cc},
				CHG =#{chg}
WHERE ins_cd = #{insCd} and fc_cd=#{fcCd}
