mapper.insert:
INSERT INTO NONOP_MGMT
			(
			NONOP_CD,
			NONOP_STT_TIME,
			NONOP_FN_TIME,
			FC_CHG,
			RSN,
			RSN_DESC,
			FC_CD
			)
    VALUES  (
    		  SEQ_FCNONOP.NEXTVAL
    		,#{nonopSttTime}
    		,#{nonopFnTime}
    		,#{fcChg}
    		,#{rsn}
    		,#{rsnDesc}
    		,#{fcCd}
    		)

mapper.update:
UPDATE FC 
SET ST = 0
WHERE FC_CD = #{fcCd}

UPDATE NONOP_MGMT
SET NONOP_FN_TIME = SYSDATE
WHERE NONOP_CD = #{nonopCd}
