mapper.insert:
INSERT INTO mt_placeod
			(mt_placeod_cd,
			placeod_qt,
			placeod_compdt,
			placeod_requester,
			placeod_chg,
			mt_cd,
			cli_cd)
    VALUES  (
    		SEQ_MT_PLACEOD.nextval
    		,#{placeodQt}
    		,#{placeodCompdt}
    		,#{placeodRequester}
    		,#{placeodChg}
    		,#{mtCd}
    		,#{cliCd}
    		)

mapper.update:
UPDATE  mt_placeod
SET     placeod_st = 2,
        placeod_compdt = sysdate,
        placeod_chg = '박민수 대리',
        placeod_qt = #{placeodQt}
WHERE   mt_placeod_cd = #{mtPlaceodCd}

UPDATE  mt_placeod
SET     placeod_st = 3
WHERE   mt_placeod_cd = #{mtPlaceodCd}

