mapper.select:
select od.pd_cd, p.unit, od.qt, od.due_dt, nvl(od.pdt_qt,0) pdt_qt, 0 as pre_pdt_qt, od.od_detailno from od_detail od left outer join pd p on od.pd_cd = p.pd_cd
od.od_detail_st= 1
AND od.pd_cd = #{pdCd}
AND due_dt >= #{dueDt}
order by od.due_dt

mapper.update:
UPDATE  od_detail
SET     pdt_qt = pdt_qt + #{pdtQt}
WHERE   od_detailno = #{odDetailno}
