mapper.select:
select od.pd_cd, p.unit, od.qt, od.due_dt, 0 as pre_pdt_qt, od.od_detailno, 0 as tot_qt from od_detail od left outer join pd p on od.pd_cd = p.pd_cd
AND od.pd_cd = #{pdCd}
AND due_dt >= #{dueDt}
order by od.due_dt

mapper.update:
update PDTPLAN 
SET wk_plan_stt_dt=#{wkPlanSttDt}, od_name=#{odName}
where pdt_plan_cd = #{pdtPlanCd}

update PDT_PLAN_DETAIL 
SET qt = #{qt}, due_dt=#{dueDt}
where pdt_plan_detail_no = #{pdtPlanDetailNo}
