mapper.update:
	    UPDATE pack_ck
           SET ck_st = 1
         WHERE pack_ck_no = #{packCkNo}

mapper.delete:
		UPDATE pack_ck
           SET ck_st = 0
         WHERE pack_ck_no = #{packCkNo}

mapper.update:
update pdt_inst_detail
set comp_st=4
where pdt_inst_detail_no = #{pdtInstDetailNo}

update pdt_hist
set pack_qual_st=1
where pdt_inst_detail_no = #{pdtInstDetailNo}
