mapper.update:
     UPDATE mf_ck
        SET first_cc = #{firstCc}
			,second_cc = #{secondCc}
			,third_cc = #{thirdCc}
			,fourth_cc = #{fourthCc}
			,fifth_cc = #{fifthCc}
			,cc_avg = #{ccAvg}
			,mfck_res = #{mfckRes}
			,ck_dt = sysdate
			,ck_chg = '최수영 대리'
	  WHERE mf_ck_no = #{mfCkNo}

	    UPDATE mf_ck
           SET ck_st = 1
         WHERE mf_ck_no = #{mfCkNo}

mapper.delete:
		UPDATE mf_ck
           SET ck_st = 0
         WHERE mf_ck_no = #{mfCkNo}

mapper.update:
update pdt_hist
set mf_qual_st=1
where pdt_inst_detail_no = #{pdtInstDetailNo}

update pdt_inst_detail
set comp_st=6
where pdt_inst_detail_no = #{pdtInstDetailNo}
