mapper.update:
     UPDATE mt_ck
        SET first_mc = #{firstMc}
			,second_mc = #{secondMc}
			,third_mc = #{thirdMc}
			,fourth_mc = #{fourthMc}
			,fifth_mc = #{fifthMc}
			,mc_avg = #{mcAvg}
			,mc_res = #{mcRes}
			,ck_dt = sysdate
	  WHERE mt_ck_no = #{mtCkNo}

			UPDATE mt_placeod
               SET PLACEOD_ST = 5
             WHERE mt_placeod_cd = #{mtPlaceodCd}
